CREATE TABLE beads (shape VARCHAR(100) NOT NULL, type VARCHAR(100) NOT NULL, color VARCHAR(40) NOT NULL, finishing_effect VARCHAR(100) NOT NULL, size FLOAT NOT NULL, amount SMALLINT NOT NULL, price FLOAT NOT NULL, other JSON DEFAULT('[]'), CONSTRAINT bead_key PRIMARY KEY (shape, type, color, finishing_effect))
CREATE TABLE strings (string_id INTEGER PRIMARY KEY AUTOINCREMENT, type VARCHAR(100) NOT NULL, color VARCHAR(40) NOT NULL, thickness FLOAT NOT NULL, length FLOAT NOT NULL, price FLOAT NOT NULL, other JSON DEFAULT('[]'))
CREATE TABLE blanks (blank_id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(100) NOT NULL, type VARCHAR(100) NOT NULL, color VARCHAR(40) NOT NULL, amount SMALLINT NOT NULL, price FLOAT NOT NULL, other JSON DEFAULT('[]'))
CREATE TABLE dividers (divider_id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(100) NOT NULL, shape VARCHAR(100) NOT NULL, color VARCHAR(40) NOT NULL, size FLOAT NOT NULL, amount SMALLINT NOT NULL, price FLOAT NOT NULL, other JSON DEFAULT('[]'))
CREATE TABLE charms (charm_id INTEGER PRIMARY KEY AUTOINCREMENT, name VARCHAR(100) NOT NULL, color VARCHAR(40) NOT NULL, amount SMALLINT NOT NULL, price FLOAT NOT NULL, other JSON DEFAULT('[]'))