CREATE TABLE component (component_id INTEGER PRIMARY KEY AUTOINCREMENT, material VARCHAR(100) NOT NULL, type VARCHAR (100) NOT NULL, made_off VARCHAR(100) NOT NULL, shape VARCHAR(100) NOT NULL, color VARCHAR(100) NOT NULL, finishing_effect VARCHAR(100) NOT NULL, component_size SMALLINT NOT NULL, UNIQUE (material, type, made_off, shape, color, finishing_effect, component_size))
CREATE TABLE inventory (inventory_id INTEGER PRIMARY KEY AUTOINCREMENT, component_id INTEGER NOT NULL, amount SMALLINT NOT NULL, unit_price FLOAT NOT NULL, total_price FLOAT NOT NULL, other JSON DEFAULT('[]'), add_date VARCHAR(100) NOT NULL, FOREIGN KEY (component_id) REFERENCES component(component_id) ON DELETE RESTRICT)
CREATE TABLE projects (project_id INTEGER PRIMARY KEY AUTOINCREMENT, project_name VARCHAR(100) UNIQUE NOT NULL, fringles_table_name VARCHAR(100), new_mapping_table_name VARCHAR(100), comment VARCHAR(1000))
CREATE TABLE products (product_id INTEGER PRIMARY KEY AUTOINCREMENT, project_id INTEGER, product_name VARCHAR(100) UNIQUE NOT NULL, amount INTEGER NOT NULL, type VARCHAR(100) NOT NULL, project_based SMALLINT NOT NULL, picture1 VARCHAR(100), picture2 VARCHAR(100), picture3 VARCHAR(100), fringles_table_name VARCHAR(100))
CREATE TABLE components_used (project_id INTEGER NOT NULL, product_id INTEGER NOT NULL, component_id INTEGER NOT NULL, amount SMALLINT NOT NULL, unit_price FLOAT NOT NULL, total_price FLOAT NOT NULL, FOREIGN KEY (project_id) REFERENCES project(project_id) ON DELETE RESTRICT, FOREIGN KEY (component_id) REFERENCES component(component_id) ON DELETE RESTRICT)
